import React, { useEffect, useState } from 'react';
import Footer from '../components/shared/footer/Footer';
import styled from "styled-components";
import axios from 'axios';
import { MainContainer, MainLayout, CardContainer, MainWapper, ChatButtonContainer, MainP } from '../components/pagestyles/MainPageStyle';
import MainCategory from '../components/main/MainCategory';
import Chat from '../components/chat/Chat';
import HotelCard from '../components/hotel/HotelCard';
import Banner from '../components/shared/banner/Banner';
import MainHeader from '../components/shared/header/MainHeader';

export default function MainPage() {

    const [hotelList, setHotelList] = useState([]);
    const [isChatOpen, setIsChatOpen] = useState(false);
    useEffect(() => {
        axios.get('/api/hotelList')
            .then(response => {
                console.log(response.data,"반응값보자");
                setHotelList(response.data);
            })
            .catch(error => {
                console.error('There was an error', error);
                alert('데이터를 불러오는 데 실패하였습니다. 잠시 후 다시 시도해주세요.');
            });
    },[])

    return (
        <MainLayout>
            <MainWapper>
                <MainHeader/>
                <MainContainer>
                    <div>
                    <Banner/>
                    </div>
                    <MainCategory />
                </MainContainer>
            </MainWapper>
            <MainP>Find a unique accommodation that will make your journey <br/>to escape everyday stress special</MainP>
            <CardContainer>
            <div style={{ display: 'flex', flexWrap: 'wrap', width: '100%' }}>
                    {hotelList.map((hotel, index) => (
                    <HotelCard style={{width:'calc(100% / 5)', margin: '50px'}} key={index} hotel={hotel} />
                    ))}
                </div>
                <Footer/>
            </CardContainer>
            <ChatButtonContainer>
                {!isChatOpen && 
                    <button onClick={() => setIsChatOpen(true)}>
                        <img src={require("../assets/b.png")}  alt="open chat" />
                    </button>
                }
                <Chat isChatOpen={isChatOpen} handleChatClose={() => setIsChatOpen(false)}/>
            </ChatButtonContainer>

        </MainLayout>
    );
}
