import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';

export default function CheckHotelAdminProfile () {
  const [profileInfo, setProfileInfo] = useState(null);
  const { userId } = useParams();

  useEffect(()=>{
    axios.get(`/api/myprofile/${userId}`)
      .then((response)=>{
        setProfileInfo(response.data);
      })
      .catch((error)=>{
        console.error("볼수없다에러다",error);
      });
  },[userId])

  if (!profileInfo) {
    return null; // 데이터 로딩 중이므로 아무것도 렌더링하지 않음
  }

  return (
    <div>
      <h2>Hotel Admin Profile</h2>
      {profileInfo && (
        <>
          <img src={profileInfo.photoPath || 'defaultImagePath'} alt="Hotel Admin Profile" />
          <p>{profileInfo.introText || 'No intro text available'}</p>
        </>
      )}
    </div>
  );

};
