  width: 100%;
  padding: 15px;
  overflow-y:auto; 
  flex-grow: 1;
`;
export const MessageInputContainer = styled.div`
  width: 100%;
  height: 60px;
  position: sticky; 
  bottom: 0;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 6px;
  background-color: #ff7d52;

