import React, { useEffect, useState } from 'react';
import Header from '../../src/components/shared/header/Header';
import Footer from '../components/shared/footer/Footer';
import styled from "styled-components";
import { MainContiner, MainLayout } from '../components/pagestyles/MainPageStyles';
import MainCategory from '../components/main/MainCategory';
import MainNews from '../components/main/MainNews';
import Chat from '../components/chat/Chat';
import Banner from '../components/shared/banner/Banner';
import axios from 'axios';


function HotelList({hotelList}) {
    return (
        <div>
            {hotelList.map((hotel,index) => (
                <div key={index}>
                    <h2>{hotel.hotelName}</h2>
                    <p>{hotel.hotelAddress}</p>
                </div>
            ))}
        </div>
    )
}

export default function MainPage() {

    const [hotelList, setHotelList] = useState([]);

    useEffect(() => {
        axios.get('/api/hotelList')
            .then(response => {
                console.log(response.data,"반응값보자");
                setHotelList(response.data);
            })
            .catch(error => {
                console.error('There was an error', error);
                alert('데이터를 불러오는 데 실패하였습니다. 잠시 후 다시 시도해주세요.');  // 사용자에게 알림
            });
    },[])

    return (
        <MainLayout>
            <MainContiner>
                <Header/>
                <div>
                <Banner/>
                </div>
                <MainCategory />
                {/* <Sidebar/> */}
                <MainNews />
            </MainContiner>

            <div>
                <Chat/>
                <HotelList hotelList={hotelList}/>
                <Footer/>
            </div>
        </MainLayout>
    );
}
